import React from 'react';

export default class Forms extends React.Component {
  constructor() {
    super();

    this.state = {
      name: '',
      email: '',
      cpf: '',
      adress: '',
    }
  }

  handleChange = (event) => {
    const nome = event.target.name;
    this.setState(() => {
      return {
        [nome]: event.target.value,
      }
    })
  }

  UpperCase = (event) => {    
    this.setState(() => {
      const valor = event.target.value;
      const convert = valor.toUpperCase();
      return { name: convert, }
    })
  }
  render() {
    const { name, email } = this.state;
    return (
      <div>
        <h1>formul√°rio bullet</h1>
        <label htmlFor="name">
          Nome:
          <input
            type="text"
            value={ name }
            name="name"
            id="name"
            maxlength="40"
            onChange={ this.UpperCase }
            required
          />
        </label>
        <label htmlFor="email">
          Email:
          <input
            type="email"
            value={ email }
            name="email"
            id="email"
            maxlength="50"
            required
            onChange={ this.handleChange }
          />
        </label>
        <label htmlFor="cpf">
          CPF:
          <input
            type="text"
            name="cpf"
            id="cpf"
            maxLength="11"
            required 
            onChange={this.handleChange}
            />
        </label>
        <label htmlFor="adress">
          <input type="text" value name="adress" id="adress" onChange={ this.handleChange } />
        </label>
      </div>
    );
  }
}
